INSERT INTO `quizmaster`.`question` (`category_id`, `topic_id`, `title`, `option-a`, `option-b`, `option-c`, `option-d`, `correct`) VALUES
(11, 105, 'Czy ateizm jest tożsamy z nihilizmem?', 'Tak, ateizm odrzuca wszelkie wartości i sens życia', 'Nie, ateizm to jedynie brak przekonania o istnieniu bogów', 'Tak, ateizm uznaje, że nie ma sensu poszukiwać znaczenia w życiu', 'Nie, ateizm może być łączony z różnymi systemami wartości', 'D'),
(11, 105, 'Jakie argumenty używane są przez ateistów w dyskusji na temat istnienia bogów?', 'Argument ontologiczny, argument z doskonałości, argument z moralności', 'Argument kosmologiczny, argument teleologiczny, argument z doświadczenia religijnego', 'Argument z niepewności, argument z pluralizmu religijnego, argument z tradycji', 'Argument z cierpienia, argument z niesprawiedliwości, argument z wolnej woli', 'B'),
(11, 105, 'Co to jest naturalizm metodologiczny w kontekście ateizmu?', 'Podejście badawcze zakładające, że wszystkie zjawiska mogą być wyjaśnione za pomocą naturalnych procesów', 'Doktryna twierdząca, że wszystko w świecie jest naturalne i pochodzi z natury', 'Przekonanie, że nauka jest jedynym źródłem prawdziwej wiedzy o rzeczywistości', 'Stanowisko filozoficzne odrzucające wszelkie formy teizmu', 'A'),
(11, 105, 'Który filozof był znany z formułowania argumentów ateistycznych w starożytnej Grecji?', 'Arystoteles', 'Platon', 'Demokryt', 'Epikur', 'D'),
(11, 105, 'Co to jest agnostycyzm ateistyczny?', 'Pogląd, że nie tylko brakuje przekonania o istnieniu bogów, ale także uznaje się to za nieznane lub nie do poznania', 'Stanowisko odrzucające istnienie bogów bez wykluczania możliwości ich istnienia', 'Przekonanie, że bogowie są nieśmiertelni i niezmienni', 'Pogląd, że religia jest źródłem wszelkiej moralności', 'A'),
(11, 105, 'Który z filozofów był znany z powiedzenia: "Bóg nie jest martwy, lecz nigdy nie żył"?', 'Friedrich Nietzsche', 'Bertrand Russell', 'Jean-Paul Sartre', 'Albert Camus', 'A'),
(11, 105, 'Co to jest humanizm ateistyczny?', 'Filozofia skupiająca się na godności i wartości ludzkiej, odrzucająca nadprzyrodzone źródła autorytetu', 'Stanowisko twierdzące, że tylko człowiek jest rzeczywistym bogiem', 'Doktryna uznająca, że człowiek może stać się boski poprzez osiągnięcie transcendencji', 'Przekonanie, że człowiek jest niczym więcej niż zwierzęciem', 'A'),
(11, 105, 'Kto jest autorem książki "Bóg urojony"?', 'Richard Dawkins', 'Sam Harris', 'Daniel Dennett', 'Christopher Hitchens', 'D'),
(11, 105, 'Jakie są główne tematy poruszane w książce "Bóg urojony"?', 'Krytyka religii, argumenty ateistyczne, rola nauki w społeczeństwie', 'Filozofia moralności, poszukiwanie sensu życia, rola wspólnoty', 'Analiza cudów, dyskusja o istnieniu duszy, koncepcja boskiego planu', 'Ewolucja kulturowa, historia religii, relacje międzywyznaniowe', 'A'),
(11, 105, 'Który z naukowców jest znany z powiedzenia: "Nigdy nie powiedziałem, że nauka może rozwiązać wszystkie problemy. Nigdy nie powiedziałem, że nauka jest w stanie odpowiedzieć na wszystkie pytania"?', 'Carl Sagan', 'Stephen Hawking', 'Richard Feynman', 'Neil deGrasse Tyson', 'A'),
(11, 105, 'Czy istnieją organizacje ateistyczne? Jeśli tak, podaj przykłady.', 'Tak, np. Amerykańska Liga Ateistyczna, Brytyjskie Towarzystwo Humanistyczne, Fundacja Wolności od Religii', 'Nie, ateizm to jedynie indywidualne przekonanie i nie istnieją zorganizowane grupy', 'Tak, np. Kościół Satanistyczny, Świątynia Otwartej Matki', 'Nie, organizacje ateistyczne były popularne w XIX wieku, ale obecnie nie istnieją', 'A'),
(11, 105, 'Jakie są główne cele organizacji ateistycznych?', 'Promowanie religijności, walka z nauką, głoszenie poglądów ateistycznych', 'Walka z przesądami, wspieranie edukacji, promowanie świeckości', 'Niszczenie kościołów, organizowanie protestów, obalać religijne instytucje', 'Walka z fundamentalizmem, obrona praw człowieka, separacja kościoła od państwa', 'D'),
(11, 105, 'Który z naukowców jest znany z powiedzenia: "Największym kłamstwem w historii nauki jest stwierdzenie, że nauka a religia są sobie przeciwstawne"?', 'Stephen Hawking', 'Richard Dawkins', 'Albert Einstein', 'Carl Sagan', 'C'),
(11, 105, 'Jakie są główne krytyki skierowane przez ateistów pod adresem religii?', 'Nauka i religia nie mogą współistnieć, religia jest przestarzała, religia jest źródłem nienawiści i konfliktów', 'Religia ogranicza rozwój społeczny, religia promuje fanatyzm, religia jest przesądem', 'Religia jest ułudą, nie ma naukowego dowodu na istnienie Boga, religia jest źródłem nietolerancji', 'Religia podważa moralność, jest przeszkodą dla postępu, jest źródłem ignorancji', 'C'),
(11, 105, 'Czy ateizm jest tożsamy z antyteizmem?', 'Tak, ateizm i antyteizm to synonimy', 'Nie, ateizm to brak przekonania o istnieniu bogów, podczas gdy antyteizm to aktywna niechęć lub wrogość wobec religii', 'Tak, ale antyteizm jest bardziej radykalną formą ateizmu', 'Nie, antyteizm to tylko jedna z wielu postaw ateistycznych', 'B'),
(11, 105, 'Czym różni się ateizm od agnostycyzmu?', 'Ateizm odrzuca istnienie bogów, podczas gdy agnostycyzm uznaje to za nieznane lub nie do poznania', 'Agnostycyzm to przekonanie, że nie ma sensu dyskutować o istnieniu bogów, podczas gdy ateizm jest aktywniejszy w krytyce religii', 'Agnostycyzm to forma ateizmu, która koncentruje się na moralności, podczas gdy ateizm dotyczy wyłącznie kwestii teologicznych', 'Agnostycyzm to tylko inna nazwa dla ateizmu', 'A'),
(11, 105, 'Jakie są główne argumenty agnostyków w dyskusji na temat istnienia bogów?', 'Twierdzą, że nie ma żadnych dowodów na istnienie bądź nieistnienie bogów', 'Uważają, że pytanie o istnienie bogów nie ma znaczenia dla ludzkości', 'Stwierdzają, że nauka jest jedynym źródłem wiedzy o rzeczywistości', 'Argumentują, że wiara w bogów jest szkodliwa dla społeczeństwa', 'A'),
(11, 105, 'Czy ateizm może być uznawany za religię?', 'Tak, ateizm to forma religii, która odrzuca istnienie bogów', 'Nie, ateizm to brak przekonania o istnieniu bogów, nie jest to religia', 'Tak, ale tylko w przypadku organizacji ateistycznych', 'Nie, religia musi obejmować wiarę w istnienie boskości', 'B'),
(11, 105, 'Czy ateizm może być moralny?', 'Tak, ateizm nie wyklucza możliwości moralnego postępowania', 'Nie, moralność wymaga wiary w istnienie bogów', 'Tak, ale tylko jeśli ateista kieruje się zasadami etyki ludzkiej', 'Nie, ateizm prowadzi do moralnego upadku jednostki i społeczeństwa', 'A'),
(11, 105, 'Czy ateizm może być łączony z innymi formami duchowości?', 'Tak, ateizm może być łączony z duchowością bez wiary w istnienie bogów', 'Nie, ateizm wyklucza jakąkolwiek formę duchowości', 'Tak, ale tylko w przypadku ateizmu agnostycznego', 'Nie, duchowość zawsze wymaga wiary w istnienie boskości', 'A'),
(11, 105, 'Czy ateizm jest bardziej popularny wśród naukowców?', 'Tak, większość naukowców jest ateistami', 'Nie, naukowcy mają różne przekonania religijne tak jak inne grupy społeczne', 'Tak, ale tylko w dziedzinach nauk przyrodniczych', 'Nie, naukowcy są zazwyczaj bardziej religijni niż przeciętni ludzie', 'B'),
(11, 105, 'Czy istnieje korelacja między poziomem edukacji a skłonnościami ateistycznymi?', 'Tak, osoby o wyższym poziomie wykształcenia są bardziej skłonne do bycia ateistami', 'Nie, poziom edukacji nie ma wpływu na przekonania religijne', 'Tak, ale tylko w krajach zachodnich', 'Nie, osoby o wyższym poziomie wykształcenia są zazwyczaj bardziej religijne', 'A');
