INSERT INTO `quizmaster`.`question` (`category_id`, `topic_id`, `title`, `option-a`, `option-b`, `option-c`, `option-d`, `correct`) VALUES
(12, 112, 'Jakie parametry są istotne dla skuteczności teleskopu kosmicznego?', 'Średnica zwierciadła, rozdzielczość, zakres fal elektromagnetycznych', 'Moc silnika, wytrzymałość konstrukcji, kolor obudowy', 'Waga teleskopu, rodzaj użytej stali, ilość baterii', 'Temperatura otoczenia, kształt obudowy, wielkość ekranu dotykowego', 'A'),
(12, 112, 'Co to jest krzywa rozdzielczości teleskopu kosmicznego?', 'Graficzna reprezentacja zdolności rozdzielczej w zależności od długości fali', 'Wykres przedstawiający liczbę gwiazd rozdzielonych w zależności od czasu ekspozycji', 'Schemat przedstawiający strukturę lustra głównego', 'Obrazek pokazujący zakres fal elektromagnetycznych widocznych przez teleskop', 'A'),
(12, 112, 'Jakie są główne cele badań prowadzonych za pomocą teleskopów kosmicznych?', 'Badanie gwiazd, galaktyk, planet, ciemnej materii, czarnych dziur, kosmicznego mikrofalowego tła', 'Badanie życia pozaziemskiego, podróży międzygwiezdnych, eksploracja Księżyca', 'Badanie zjawisk atmosferycznych innych planet, prognozowanie pogody kosmicznej', 'Badanie historii kosmosu, rejestrowanie przechodzących asteroid', 'A'),
(12, 112, 'Co to jest interferometria w kontekście teleskopów kosmicznych?', 'Technika pozwalająca łączyć dane z kilku teleskopów w celu uzyskania wyższej rozdzielczości', 'Badanie interferencji fal radiowych wyemitowanych przez kosmiczne obiekty', 'Pomiar odległości między teleskopami na różnych planetach', 'Tworzenie wizualizacji interferencji fal elektromagnetycznych', 'A'),
(12, 112, 'Jakie znaczenie ma redukcja szumu w obrazach uzyskiwanych przez teleskopy kosmiczne?', 'Zwiększa jakość obrazu poprzez eliminację przypadkowych zakłóceń', 'Zmniejsza wykorzystanie energii przez teleskop', 'Powoduje spadek kosztów produkcji teleskopów', 'Ułatwia komunikację z innymi teleskopami', 'A'),
(12, 112, 'Co to jest zdalne sterowanie w kontekście teleskopów kosmicznych?', 'Możliwość kontrolowania teleskopu z dowolnego miejsca na Ziemi za pomocą komputera', 'Automatyczne wykrywanie obiektów kosmicznych przez teleskop', 'Sterowanie teleskopem za pomocą głosu', 'Ręczne przestawianie teleskopu na obiekty w kosmosie', 'A'),
(12, 112, 'Jakie są główne różnice między teleskopami kosmicznymi a naziemnymi?', 'Brak atmosfery, możliwość obserwacji w szerokim zakresie fal elektromagnetycznych, lepsza rozdzielczość', 'Mniejsze rozmiary, większa mobilność, niższe koszty', 'Większa moc zbierania światła, łatwiejszy dostęp do serwisu, dłuższa żywotność', 'Brak potrzeby kalibracji, mniejsza wrażliwość na zakłócenia elektromagnetyczne, łatwiejsza obsługa', 'A'),
(12, 112, 'Jakie są główne wyzwania związane z utrzymaniem teleskopów kosmicznych?', 'Zarządzanie zasobami energetycznymi, utrzymanie stabilnej orbity, ochrona przed promieniowaniem kosmicznym', 'Zapewnienie odpowiedniej temperatury wewnątrz teleskopu, ochrona przed obcymi mikroorganizmami, kontrola satelitów', 'Testowanie wytrzymałości na wstrząsy, monitorowanie warunków atmosferycznych w przestrzeni kosmicznej, planowanie tras misji', 'Rozwiązywanie problemów związanych z łącznością, aktualizacja oprogramowania pokładowego, zapobieganie kolizjom z innymi obiektami', 'A'),
(12, 112, 'Co to jest efekt atmosferyczny, a jak wpływa na obserwacje teleskopów kosmicznych?', 'Zniekształcenie obrazu spowodowane oddziaływaniem atmosfery z promieniowaniem elektromagnetycznym; teleskopy kosmiczne unikają tego efektu', 'Zjawisko, które sprawia, że obiekty w kosmosie wydają się większe niż są w rzeczywistości; teleskopy kosmiczne wykorzystują ten efekt do zwiększenia rozdzielczości', 'Zmniejszenie jasności obserwowanych obiektów spowodowane promieniowaniem słonecznym; teleskopy kosmiczne muszą stosować dodatkowe filtry', 'Zjawisko, które sprawia, że obiekty w kosmosie wydają się mniejsze niż są w rzeczywistości; teleskopy kosmiczne muszą to uwzględnić przy kalibracji', 'A'),
(12, 112, 'Jakie są najważniejsze cele misji teleskopu Hubble’a?', 'Badanie odległych galaktyk, planetoid, gwiazd, mgławic, czarnych dziur, mierzenie wieku Wszechświata', 'Śledzenie trajektorii asteroid zbliżających się do Ziemi, monitorowanie aktywności słonecznej, badanie atmosfery Marsa', 'Odkrywanie nowych księżyców Jowisza, analiza struktury chmur na Wenus, badanie zjawisk sejsmicznych na Księżycu', 'Badanie oceanów na Europie, analiza składu atmosfery Uranu, monitorowanie pola magnetycznego Merkurego', 'A'),
(12, 112, 'Dlaczego teleskop Hubble’a jest umieszczony na orbicie okołoziemskiej?', 'Aby uniknąć zakłóceń atmosferycznych, które mogą zniekształcać obserwacje', 'Aby był bliżej Ziemi, co ułatwia serwisowanie i naprawy', 'Aby umożliwić łatwiejszy dostęp astronautom do zbieranych danych', 'Aby możliwe było wykonywanie zdjęć z bliska innych planet Układu Słonecznego', 'A'),
(12, 112, 'Co to jest kamera termowizyjna, a jakie ma zastosowanie w teleskopach kosmicznych?', 'Urządzenie służące do rejestracji promieniowania podczerwonego; wykorzystywane do obserwacji obiektów o niskiej temperaturze', 'Urządzenie służące do rejestrowania promieniowania ultrafioletowego; wykorzystywane do obserwacji gorących gwiazd', 'Urządzenie służące do rejestracji fal radiowych; wykorzystywane do obserwacji pulsarów', 'Urządzenie służące do rejestrowania promieniowania rentgenowskiego; wykorzystywane do obserwacji czarnych dziur', 'A'),
(12, 112, 'Jakie technologie umożliwiają wykonanie wysokiej jakości obrazów przez teleskopy kosmiczne?', 'Zastosowanie układów optycznych złożonych z luster o bardzo dużej precyzji, czujników CCD o wysokiej rozdzielczości, systemów chłodzenia', 'Wykorzystanie soczewek akromatycznych, rur optycznych z włókna węglowego, kamer wideo o dużej czułości', 'Zastosowanie filtrów polaryzacyjnych, kamer termowizyjnych, technologii druku 3D', 'Wykorzystanie wyświetlaczy holograficznych, paneli fotowoltaicznych, technologii biometrii', 'A'),
(12, 112, 'Co to jest teleskop interferometryczny, a jakie są jego zalety?', 'Teleskop składający się z wielu mniejszych teleskopów połączonych w sieć; umożliwia uzyskanie bardzo wysokiej rozdzielczości przestrzennej', 'Teleskop wykorzystujący technologię komputerową do generowania obrazów trójwymiarowych; umożliwia uzyskanie efektu immersji', 'Teleskop zbudowany z materiałów o bardzo niskiej współczynniku załamania; umożliwia obserwacje w zakresie ultrafioletu', 'Teleskop z automatycznym systemem śledzenia obiektów; umożliwia wykonywanie długich ekspozycji', 'A'),
(12, 112, 'Jakie są główne rodzaje teleskopów kosmicznych?', 'Optyczne, rentgenowskie, podczerwone, gamma, radiowe', 'Reflektorowe, refrakcyjne, katadioptryczne, Cassegraina, Newtona', 'Schmidta-Cassegraina, Maksutowa, Gregoriana, Mersenne’a, Rumak-Herschela', 'Newtona, Keplera, Galileusza, Huygensa, Maksutowa', 'A'),
(12, 112, 'Co to jest teleskop kosmiczny Jamesa Webba, a jakie są jego cele?', 'Następca teleskopu Hubble’a, mający za zadanie obserwacje w podczerwieni, badanie formowania się gwiazd i galaktyk we wczesnym Wszechświecie', 'Pierwszy teleskop kosmiczny wysłany na orbitę przez NASA, mający za zadanie obserwacje w ultrafiolecie, badanie aktywności słonecznej', 'Teleskop będący częścią międzynarodowego projektu ESA, mający za zadanie poszukiwanie życia pozaziemskiego, badanie atmosfery egzoplanet', 'Teleskop będący wspólnym przedsięwzięciem NASA i Roskosmosu, mający za zadanie obserwacje w zakresie rentgenowskim, badanie czarnych dziur', 'A'),
